import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [image, setImage] = useState(null);
  const [profile, setProfile] = useState("acne");
  const [customProfile, setCustomProfile] = useState("");
  const [customIngredients, setCustomIngredients] = useState("");
  const [results, setResults] = useState(null);
  const [preview, setPreview] = useState(null);

  const resetForm = () => {
    setImage(null);
    setProfile("acne");
    setCustomProfile("");
    setCustomIngredients("");
    setResults(null);
    setPreview(null);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const finalProfile = profile === "custom" ? customProfile.toLowerCase() : profile;

    // First: Add custom ingredients to DB if any
    if (customIngredients.trim()) {
      const ingredients = customIngredients.split(',').map(i => i.trim());
      for (let ing of ingredients) {
        try {
          await axios.post("http://localhost:5000/add-ingredient", {
            profile: finalProfile,
            ingredient: ing
          });
        } catch (err) {
          console.error(`Failed to add ${ing}:`, err);
        }
      }
    }

    // Then: Upload image to check ingredients
    const formData = new FormData();
    formData.append("image", image);
    formData.append("skin_profile", finalProfile);

    try {
      const res = await axios.post("http://localhost:5000/upload", formData);
      setResults(res.data);
    } catch (err) {
      console.error("Upload Error:", err);
      if (err.response) {
        alert(`Server error: ${err.response.data.error || "Something went wrong"}`);
      } else if (err.request) {
        alert("No response from backend. Is it running?");
      } else {
        alert("Error uploading or connecting to backend.");
      }
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) setPreview(URL.createObjectURL(file));
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>✨ AllerLens – Skincare Ingredient Checker</h1>

      <form onSubmit={handleSubmit} style={styles.form}>
        <label style={styles.label}>Upload Product Image</label>
        <input
          type="file"
          onChange={handleImageChange}
          accept="image/*"
          required
          style={styles.input}
        />

        {preview && <img src={preview} alt="Preview" style={styles.preview} />}

        <label style={styles.label}>Choose Skin Profile</label>
        <select
          onChange={(e) => setProfile(e.target.value)}
          value={profile}
          style={styles.select}
        >
          <option value="acne">Acne-Prone</option>
          <option value="allergy">Allergy-Sensitive</option>
          <option value="hormonal">Hormonal (PCOS)</option>
          <option value="custom">Other (Custom)</option>
        </select>

        {profile === "custom" && (
          <input
            type="text"
            placeholder="Enter your custom skin profile"
            value={customProfile}
            onChange={(e) => setCustomProfile(e.target.value)}
            style={styles.input}
            required
          />
        )}

        <label style={styles.label}>Custom Ingredients (comma-separated)</label>
        <input
          type="text"
          placeholder="e.g. lavender oil, coconut oil"
          value={customIngredients}
          onChange={(e) => setCustomIngredients(e.target.value)}
          style={styles.input}
        />

        <div style={{ display: "flex", gap: "10px" }}>
          <button type="submit" style={styles.button}>Check Ingredients</button>
          <button type="button" onClick={resetForm} style={styles.refreshButton}>🔄 Refresh</button>
        </div>
      </form>

      {results && (
        <div style={styles.resultBox}>
          <h2>🧪 Flagged Ingredients</h2>
          <ul style={styles.resultList}>
            {results.flagged_ingredients.length > 0 ? (
              results.flagged_ingredients.map((item, idx) => (
                <li key={idx} style={styles.flaggedItem}>{item}</li>
              ))
            ) : (
              <li style={{ color: "green" }}>No harmful ingredients detected 🎉</li>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: "40px",
    maxWidth: "700px",
    margin: "auto",
    fontFamily: "'Segoe UI', sans-serif",
    background: "#fdfdfd",
    borderRadius: "14px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.08)",
    animation: "fadeIn 0.6s ease-in-out"
  },
  title: {
    textAlign: "center",
    color: "#2b2b2b",
    marginBottom: "30px",
    fontWeight: "bold",
    fontSize: "28px"
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "20px"
  },
  label: {
    fontWeight: "600",
    marginBottom: "5px",
    color: "#444"
  },
  input: {
    padding: "10px",
    fontSize: "16px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    transition: "all 0.3s ease-in-out",
    outline: "none"
  },
  select: {
    padding: "10px",
    fontSize: "16px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "border 0.3s",
    outline: "none"
  },
  button: {
    padding: "12px",
    backgroundColor: "#4CAF50",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    fontSize: "16px",
    fontWeight: "bold",
    cursor: "pointer",
    transition: "transform 0.2s, background 0.3s"
  },
  refreshButton: {
    padding: "12px",
    backgroundColor: "#f44336",
    color: "#fff",
    border: "none",
    borderRadius: "10px",
    fontSize: "16px",
    fontWeight: "bold",
    cursor: "pointer"
  },
  preview: {
    marginTop: "15px",
    maxWidth: "100%",
    borderRadius: "12px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)"
  },
  resultBox: {
    marginTop: "30px",
    padding: "20px",
    backgroundColor: "#f9f9f9",
    border: "1px solid #eee",
    borderRadius: "12px"
  },
  resultList: {
    listStyleType: "circle",
    paddingLeft: "20px",
    marginTop: "10px"
  },
  flaggedItem: {
    color: "#d32f2f",
    fontWeight: "500"
  }
};

export default App;
