import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [image, setImage] = useState(null);
  const [profile, setProfile] = useState("acne");
  const [results, setResults] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("image", image);
    formData.append("skin_profile", profile);

    try {
      const res = await axios.post("http://localhost:5000/upload", formData);
      setResults(res.data);
    } catch (err) {
      alert("Error uploading or connecting to backend.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>AllerLens - Skincare Ingredient Checker</h1>
      <form onSubmit={handleSubmit}>
        <input type="file" onChange={(e) => setImage(e.target.files[0])} accept="image/*" required />
        <select onChange={(e) => setProfile(e.target.value)} value={profile}>
          <option value="acne">Acne-Prone</option>
          <option value="allergy">Allergy-Sensitive</option>
          <option value="hormonal">Hormonal (PCOS)</option>
        </select>
        <button type="submit">Check</button>
      </form>

      {results && (
        <div style={{ marginTop: "20px" }}>
          <h2>Flagged Ingredients</h2>
          <ul>
            {results.flagged_ingredients.length > 0 ? (
              results.flagged_ingredients.map((item, idx) => <li key={idx}>{item}</li>)
            ) : (
              <li>No harmful ingredients detected ðŸŽ‰</li>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
