import React, { useState, useRef, useEffect } from "react";
import axios from "axios";
import Particles from "react-tsparticles";

function App() {
  const [image, setImage] = useState(null);
  const [customIngredients, setCustomIngredients] = useState("");
  const [results, setResults] = useState(null);
  const [preview, setPreview] = useState(null);
  const [ingredientMessage, setIngredientMessage] = useState("");
  const [scanning, setScanning] = useState(false);
  const [capturedFrames, setCapturedFrames] = useState(0);
  const [framesBuffer, setFramesBuffer] = useState([]);
  const [processing, setProcessing] = useState(false);

  const imageInputRef = useRef(null);
  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const maxFrames = 15;

  const startLiveScan = async () => {
    setScanning(true);
    setCapturedFrames(0);
    setFramesBuffer([]);
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();
    } catch (err) {
      alert("Could not access camera. Please allow permissions.");
      setScanning(false);
    }
  };

  const stopLiveScan = async () => {
    setScanning(false);
    let tracks = videoRef.current?.srcObject?.getTracks();
    if (tracks) tracks.forEach((track) => track.stop());

    if (framesBuffer.length > 0) {
      try {
        setProcessing(true);
        const res = await axios.post("http://localhost:5000/upload-frames", { frames: framesBuffer });
        setResults({ flagged_ingredients: res.data.flagged_ingredients, all_text: res.data.all_text });
        setFramesBuffer([]);
        setCapturedFrames(0);
      } catch (err) {
        console.error("Live scan upload error:", err);
        alert("Failed to process live scan frames.");
      } finally {
        setProcessing(false);
      }
    }
  };

  useEffect(() => {
    let interval;
    if (scanning) {
      interval = setInterval(() => {
        if (capturedFrames >= maxFrames) {
          stopLiveScan();
          clearInterval(interval);
          return;
        }
        captureFrame();
      }, 300);
    }
    return () => clearInterval(interval);
  }, [scanning, capturedFrames]);

  const captureFrame = () => {
    if (!videoRef.current || !canvasRef.current) return;
    const ctx = canvasRef.current.getContext("2d");
    ctx.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);
    const dataUrl = canvasRef.current.toDataURL("image/jpeg");
    setFramesBuffer((prev) => [...prev, dataUrl]);
    setCapturedFrames((c) => c + 1);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) return alert("Please upload an image.");
    const formData = new FormData();
    formData.append("image", image);
    try {
      setProcessing(true);
      const res = await axios.post("http://localhost:5000/upload", formData);
      setResults({ flagged_ingredients: [...new Set(res.data.flagged_ingredients)] });
    } catch (err) {
      console.error("Upload Error:", err);
      alert("Something went wrong while uploading.");
    } finally {
      setProcessing(false);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) setPreview(URL.createObjectURL(file));
  };

  const handleAddIngredients = async () => {
    if (!customIngredients.trim()) return;
    const ingredients = customIngredients.split(",").map(i => i.trim().toLowerCase()).filter(i => i.length > 0);
    const added = [];
    try {
      for (let ing of ingredients) {
        await axios.post("http://localhost:5000/add-ingredient", { ingredient: ing });
        added.push(ing);
      }
      setIngredientMessage(`✅ Added: ${added.join(", ")}`);
      setCustomIngredients("");
    } catch (err) {
      console.error(err);
      alert("Failed to add ingredient(s).");
    }
  };

  const handleClear = () => {
    setResults(null);
    setImage(null);
    setPreview(null);
    setCustomIngredients("");
    setIngredientMessage("");
    setFramesBuffer([]);
    setCapturedFrames(0);
    if (imageInputRef.current) imageInputRef.current.value = "";
    stopLiveScan();
  };

  return (
    <div style={{ position: "relative", minHeight: "100vh", overflow: "hidden", background: "#9e7ca5" }}>
      <Particles
        style={{ position: "absolute", top: 0, left: 0, zIndex: 0 }}
        options={{
          background: { color: { value: "#9e7ca5" } },
          fpsLimit: 60,
          interactivity: { 
            events: { 
              onHover: { enable: true, mode: "repulse" }, 
              onClick: { enable: true, mode: "push" }, 
              resize: true 
            }, 
            modes: { 
              repulse: { distance: 100, duration: 0.4 }, 
              push: { quantity: 4 } 
            } 
          },
          particles: { 
            color: { value: ["#f1c0d1", "#d9a1c1", "#b07fa0", "#f3e1ea"] }, 
            links: { enable: true, color: "#ffffff", distance: 120, opacity: 0.2, width: 1 }, 
            move: { enable: true, speed: 0.3, direction: "none", random: true, outModes: "bounce" }, 
            number: { value: 50, density: { enable: true, area: 800 } }, 
            opacity: { value: 0.3 }, 
            shape: { type: "circle" }, 
            size: { value: { min: 1, max: 3 } } 
          },
          detectRetina: true
        }}
      />

      <div style={styles.gradientOverlay}></div>
      <div style={styles.pageContainer}>
        <div style={styles.container}>
          <h1 style={styles.title}>✨ AllerLens – Ingredient Checker</h1>
          <form onSubmit={handleSubmit} style={styles.form}>
            <label style={styles.label}>Upload Product Image</label>
            <input type="file" onChange={handleImageChange} accept="image/*" style={styles.input} ref={imageInputRef} />
            {preview && <img src={preview} alt="Preview" style={styles.preview} />}
            <label style={styles.label}>Custom Ingredients (comma-separated)</label>
            <input type="text" placeholder="e.g. lavender oil, coconut oil" value={customIngredients} onChange={e => setCustomIngredients(e.target.value)} style={styles.input} />

            <button type="button" style={{ ...styles.button, backgroundColor: "#d9a1c1", color: "#3b1f3b" }} onClick={handleAddIngredients}>➕ Add Ingredients to DB</button>
            <button type="submit" style={{ ...styles.button, backgroundColor: "#f1c0d1", color: "#3b1f3b" }}>🔍 Check Ingredients</button>
            <button type="button" style={{ ...styles.clearButton, backgroundColor: "#b07fa0", color: "#ffffff" }} onClick={handleClear}>🔄 Clear</button>
          </form>

          {!scanning ?
            <button style={{ ...styles.button, marginTop: "15px", backgroundColor: "#f3e1ea", color: "#3b1f3b" }} onClick={startLiveScan}>📸 Start Live Scan</button>
            :
            <button style={{ ...styles.clearButton, marginTop: "15px", backgroundColor: "#b07fa0", color: "#fff" }} onClick={stopLiveScan}>⏹ Stop Live Scan</button>
          }

          {scanning && (
            <div style={styles.videoContainer}>
              <video ref={videoRef} width="400" height="300" style={styles.video} />
              <canvas ref={canvasRef} width="400" height="300" style={{ display: "none" }} />
              <p style={styles.videoText}>📷 Captured {capturedFrames}/{maxFrames} frames</p>
            </div>
          )}

          {processing && <p style={styles.processingText}>⏳ Processing ingredients...</p>}
          {ingredientMessage && <p style={styles.ingredientMsg}>{ingredientMessage}</p>}

          {results && (
            <div style={styles.resultBox}>
              <h2>🧪 Flagged Ingredients</h2>
              <ul style={styles.resultList}>
                {results.flagged_ingredients.length > 0 ?
                  results.flagged_ingredients.map((item, idx) => <li key={idx} style={styles.flaggedItem}>{item}</li>)
                  :
                  <li style={{ color: "#000000" }}>No harmful ingredients detected 🎉</li>
                }
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

