import React, { useState, useRef } from "react";
import axios from "axios";

function App() {
  const [image, setImage] = useState(null);
  const [customIngredients, setCustomIngredients] = useState("");
  const [results, setResults] = useState(null);
  const [preview, setPreview] = useState(null);
  const [ingredientMessage, setIngredientMessage] = useState("");
  const [liveScan, setLiveScan] = useState(false);
  const [scanning, setScanning] = useState(false);

  const videoRef = useRef(null);
  const imageInputRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) {
      alert("Please upload an image.");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);

    try {
      const res = await axios.post("http://localhost:5000/upload", formData);
      const uniqueFlagged = [...new Set(res.data.flagged_ingredients)];
      setResults({ ...res.data, flagged_ingredients: uniqueFlagged });
    } catch (err) {
      console.error("Upload Error:", err);
      alert("Something went wrong while uploading.");
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) setPreview(URL.createObjectURL(file));
  };

  const handleAddIngredients = async () => {
    if (!customIngredients.trim()) return;

    const ingredients = customIngredients
      .split(",")
      .map((i) => i.trim().toLowerCase())
      .filter((i) => i.length > 0);

    const added = [];
    try {
      for (let ing of ingredients) {
        await axios.post("http://localhost:5000/add-ingredient", {
          ingredient: ing,
        });
        added.push(ing);
      }
      setIngredientMessage(`‚úÖ Added: ${added.join(", ")}`);
      setCustomIngredients("");
    } catch (err) {
      console.error(err);
      alert("Failed to add ingredient(s).");
    }
  };

  const handleClear = () => {
    setResults(null);
    setImage(null);
    setPreview(null);
    setCustomIngredients("");
    setIngredientMessage("");
    setLiveScan(false);
    setScanning(false);

    if (imageInputRef.current) {
      imageInputRef.current.value = "";
    }

    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((t) => t.stop());
    }
  };

  // Live scanning logic
  const startLiveScan = async () => {
    setLiveScan(true);
    setScanning(true);
    setResults(null);

    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;

      let frameCount = 0;
      const maxFrames = 5; // auto stop after 5 frames

      const capture = async () => {
        if (!scanning || frameCount >= maxFrames) {
          stopLiveScan();
          return;
        }

        const canvas = document.createElement("canvas");
        canvas.width = videoRef.current.videoWidth;
        canvas.height = videoRef.current.videoHeight;
        canvas
          .getContext("2d")
          .drawImage(videoRef.current, 0, 0, canvas.width, canvas.height);

        canvas.toBlob(async (blob) => {
          if (!blob) return;
          const formData = new FormData();
          formData.append("image", blob, "frame.jpg");

          try {
            const res = await axios.post("http://localhost:5000/upload", formData);
            const uniqueFlagged = [...new Set(res.data.flagged_ingredients)];
            setResults({ ...res.data, flagged_ingredients: uniqueFlagged });
          } catch (err) {
            console.error("Live scan error:", err);
          }
        });

        frameCount++;
        setTimeout(capture, 1500); // capture every 1.5s
      };

      setTimeout(capture, 1000);
    } catch (err) {
      console.error("Camera error:", err);
      alert("Could not access camera.");
      setScanning(false);
    }
  };

  const stopLiveScan = () => {
    setScanning(false);
    if (videoRef.current && videoRef.current.srcObject) {
      videoRef.current.srcObject.getTracks().forEach((t) => t.stop());
    }
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.title}>‚ú® AllerLens ‚Äì Ingredient Checker</h1>

      {/* Choose Mode */}
      {!liveScan ? (
        <form onSubmit={handleSubmit} style={styles.form}>
          <label style={styles.label}>Upload Product Image</label>
          <input
            type="file"
            onChange={handleImageChange}
            accept="image/*"
            style={styles.input}
            ref={imageInputRef}
          />

          {preview && <img src={preview} alt="Preview" style={styles.preview} />}

          <label style={styles.label}>Custom Ingredients (comma-separated)</label>
          <input
            type="text"
            placeholder="e.g. lavender oil, coconut oil"
            value={customIngredients}
            onChange={(e) => setCustomIngredients(e.target.value)}
            style={styles.input}
          />

          <button
            type="button"
            style={{ ...styles.button, backgroundColor: "#2196F3" }}
            onClick={handleAddIngredients}
          >
            ‚ûï Add Ingredients to DB
          </button>

          <button type="submit" style={styles.button}>
            üîç Check Ingredients
          </button>

          <button
            type="button"
            style={{ ...styles.button, backgroundColor: "#6a1b9a" }}
            onClick={startLiveScan}
          >
            üì∑ Start Live Scan
          </button>

          <button
            type="button"
            style={styles.clearButton}
            onClick={handleClear}
          >
            üîÑ Clear
          </button>
        </form>
      ) : (
        <div style={styles.form}>
          <video ref={videoRef} autoPlay playsInline style={styles.preview}></video>

          {scanning ? (
            <button
              type="button"
              style={styles.clearButton}
              onClick={stopLiveScan}
            >
              ‚èπ Stop Live Scan
            </button>
          ) : (
            <button
              type="button"
              style={styles.clearButton}
              onClick={handleClear}
            >
              üîÑ Reset
            </button>
          )}
        </div>
      )}

      {ingredientMessage && (
        <p style={{ color: "green", marginTop: "10px" }}>{ingredientMessage}</p>
      )}

      {results && (
        <div style={styles.resultBox}>
          <h2>üß™ Flagged Ingredients</h2>
          <ul style={styles.resultList}>
            {results.flagged_ingredients.length > 0 ? (
              results.flagged_ingredients.map((item, idx) => (
                <li key={idx} style={styles.flaggedItem}>
                  {item}
                </li>
              ))
            ) : (
              <li style={{ color: "green" }}>
                No harmful ingredients detected üéâ
              </li>
            )}
          </ul>
        </div>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: "40px",
    maxWidth: "700px",
    margin: "auto",
    fontFamily: "'Segoe UI', sans-serif",
    background: "#fdfdfd",
    borderRadius: "14px",
    boxShadow: "0 10px 25px rgba(0,0,0,0.08)",
  },
  title: {
    textAlign: "center",
    color: "#2b2b2b",
    marginBottom: "30px",
    fontWeight: "bold",
    fontSize: "28px",
  },
  form: {
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  label: { fontWeight: "600", color: "#444" },
  input: {
    padding: "10px",
    fontSize: "16px",
    border: "1px solid #ddd",
    borderRadius: "8px",
  },
  button: {
    padding: "12px",
    backgroundColor: "#4CAF50",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    fontSize: "16px",
    fontWeight: "bold",
    cursor: "pointer",
  },
  clearButton: {
    padding: "10px",
    backgroundColor: "#f44336",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    fontSize: "14px",
    fontWeight: "bold",
    cursor: "pointer",
  },
  preview: {
    maxWidth: "100%",
    borderRadius: "10px",
    boxShadow: "0 4px 10px rgba(0,0,0,0.1)",
  },
  resultBox: {
    marginTop: "30px",
    padding: "20px",
    backgroundColor: "#f9f9f9",
    border: "1px solid #eee",
    borderRadius: "12px",
  },
  resultList: { listStyleType: "disc", paddingLeft: "20px" },
  flaggedItem: { color: "#d32f2f", fontWeight: "500" },
};

export default App;
